package main

import (
	"encoding/base64"
	"image"
	"image/color"
	"image/png"
	"strings"

	"github.com/go-glx/vgl"
	"github.com/go-glx/vgl/glm"
)

const gopher = ``

var gImg image.Image

func init() {
	img, err := png.Decode(base64.NewDecoder(base64.StdEncoding, strings.NewReader(gopher)))
	if err != nil {
		panic(err)
	}

	gImg = img
}

func e3Points(rnd *vgl.Render) {
	// this demo will draw 5 gopher images
	// in most ineffective way (pixel by pixel)
	// is total of 22500 pixel

	// pixel drawing is fast enough for rendering ~1000 times per frame
	// but not recommended using this for any case, except debug gizmos
	// for better performance, use instancing:
	// - strictly one after another
	// - without blend mode / surface change
	// - without another drawing between calls

	const offset = 100

	for y := gImg.Bounds().Min.Y; y < gImg.Bounds().Max.Y; y++ {
		for x := gImg.Bounds().Min.X; x < gImg.Bounds().Max.X; x++ {
			col := color.GrayModel.Convert(gImg.At(x, y)).(color.Gray)

			for i := 1; i <= 5; i++ {
				rnd.Draw2dPoint(&vgl.Params2dPoint{
					Pos:   glm.Local2D{int32(x + (i * offset)), int32(y + offset)},
					Color: glm.NewColor(col.Y, col.Y, col.Y, 255),
				})
			}
		}
	}
}
